import React, { Component } from 'react'

export default class Ccomponent extends Component {
  constructor(props) {
    super(props)

    this.state = {
    count:0
    };
    this.increment = this.increment.bind(this);//принажатии на кнопку появится надпись "люблю поесть еду"
    this.decrement = this.decrement.bind(this);
    this.reset = this.reset.bind(this);
  }

  increment() {
    this.setState(state => ({
      count:state.count+1
    }));
  }
  decrement() {
    this.setState(state => ({
     count: state.count-1
    }));
  }
  reset(){
    this.setState({
      count:0
    })
  }

  render() {
    if (this.state.visibility) {


      return (
        <div>
          <h1>еда плоха
          </h1>
          <button onClick={this.handleClick}>Click</button>

        </div>
      )
    }
    else{
      return(
        <div>
          <button onClick={this.handleClick}>Click</button>
        </div>
      )
    }
  }
}
