import React, { Component } from 'react'

export default class Ccomponent extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       visibility:false
    };
    this.handleClick=this.handleClick.bind(this);//принажатии на кнопку появится надпись "люблю поесть еду"

  }

  handleClick(){
    this.setState( state =>({
     visibility:!this.state.visibility
    }));
  }
  
  render() {
    return (
      <div>
        <h1>class Component{
        this.state.name
  }
        </h1>
        <button onClick={this.handleClick}>Click</button>

      </div>
    )
  }
}
